import React, { Component } from 'react';
import './Card.css';


class Card extends Component {
  render() {
    const {title, subtitle, icon} = this.props;
    return (
      <div className="card">
        <img className="card_bg_image" src='../assets/card_bg.png' alt='pic'/>
        <div className="card_title">RESPONSIVE DESIGN</div>
        <div className="card_subtitle">Make sure your website is suitable for all devices, and looks great on different screen sizes.
So your customers can reach your website with their smartphones.</div>
        <i className="icon fas fa-tablet-alt"></i>
      </div>
    );
  }
}

export default Card;
