import React, { Component } from "react";
import "./News.css";
import axios from "axios";

class News extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: []
    };
  }
  componentDidMount() {
    axios
      .get(
        `https://newsapi.org/v2/top-headlines?sources=techcrunch&apiKey=5a86604b83ed474991ed1221830dd50b`
      )
      .then(res => {
        const articles = res.data;
        this.setState({ articles: articles.articles });
      });
  }

  render() {
    const topicOne =
      this.state.articles.length !== 0 ? this.state.articles[0] : "TOPIC ONE";
    const topicTwo =
      this.state.articles.length !== 0 ? this.state.articles[1] : "TOPIC TWO";
    const topicThree =
      this.state.articles.length !== 0 ? this.state.articles[2] : "TOPIC THREE";

    console.log(topicOne);

    const topbg = {
      backgroundImage: "url('../assets/newsbg.jpg')"
    };
    return (
      <div className="news">
        <div className="news-top" style={topbg}>
          <div className="news-top-first-title">TechCrunch</div>
          <div className="news-top-second-title">NEWSLETTER</div>
        </div>

        <div className="news-date">
          <p>Date: August 19, 2018.</p>
        </div>

        <div className="news-content">
          <div className="news-content-first">
            <div className="topic-wraper one">
              <div className="topic">{topicOne.title}</div>
            </div>
            <div className="news-text">{topicOne.description}</div>
            <img
              className="news-content-first-pic news-image"
              alt="news-content-left-pic"
              src={topicOne.urlToImage}
            />
          </div>

          <div className="news-content-mid">
            <div className="topic-wraper two ">
              <div className="news-content-mid-title topic">
                {topicTwo.title}
              </div>
            </div>
            <img
              className="news-content-mid-pic news-image"
              alt="news-content-mid-pic"
              src={topicTwo.urlToImage}
            />
            <div className="news-text">{topicTwo.description}</div>
          </div>

          <div className="news-content-third">
            <div className="topic-wraper three">
              <div className="news-content-third-title topic">
                {topicThree.title}
              </div>
            </div>

            <img
              className="news-content-third-pic news-image"
              alt="item-image"
              src={topicThree.urlToImage}
            />
            <div className="news-text">{topicThree.description}</div>
          </div>
        </div>
      </div>
    );
  }
}

export default News;
