import React, { Component } from "react";
import "./News.css";
import axios from "axios";
import SecNavbar from "../SecNavbar";

class News extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      date: ""
    };
    this.getDate = this.getDate.bind(this);
  }

  componentDidMount() {
    axios
      .get(
        `https://newsapi.org/v2/top-headlines?sources=techcrunch&apiKey=5a86604b83ed474991ed1221830dd50b`
      )
      .then(res => {
        const articles = res.data;
        this.setState({ articles: articles.articles });
      });
  }

  getRow() {
    let row = [];
    for (let i = 0; i < 6; i++) {
      let article =
        this.state.articles.length !== 0 ? this.state.articles[i] : "TOPIC ";
      row.push(
        <a href={article.url}>
          <div className="item">
            <img
              className="news-image"
              alt="news-content-left"
              src={article.urlToImage}
            />
            <div className="topic">{article.title}</div>
            <div className="news-author">
              <b>By: </b>
              <em>{article.author}</em>
            </div>
            <div className="news-author">
              <b>Published: </b>
              <em>{this.state.date}</em>
            </div>

            <div className="news-text">{article.description}</div>
          </div>
        </a>
      );
    }
    return row;
  }

  getDate() {
    var today = new Date();
    var dd = today.getDate();
    var mm = today.getMonth() + 1; //January is 0!
    var yyyy = today.getFullYear();

    if (dd < 10) {
      dd = "0" + dd;
    }

    if (mm < 10) {
      mm = "0" + mm;
    }

    today = mm + "/" + dd + "/" + yyyy;

    this.setState({ date: today });
  }

  render() {
    if (this.state.date === "") this.getDate();

    // let cardsRowOne = [];
    // let cardsRowTwo = [];
    let row = this.getRow();

    // for (let i = 0; i < 3; i++) {
