import React, { Component } from "react";
import "./Tabs.css";
import TabContent from "./TabContent";

class Tabs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selected: "0",
      info: [
        {
          title: "",
          text: "",
          img: "",
          id: "0"
        },
        {
          title: "",
          text: "",
          img: "",
          id: "1"
        },
        {
          title: "",
          text: "",
          img: "",
          id: "2"
        },
        {
          title: "",
          text: "",
          img: "",
          id: "3"
        }
      ]
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    this.setState({
      selected: e.target.id
    });
  }

  render() {
    const content = this.state.info.map(el => {
      if (this.state.selected === el.id) {
        return (
          <TabContent title={el.title} text={el.text} img={el.img} id={el.id} />
        );
      }
    });
    return (
      <div className="tabs_wrap">
        <div className="tabs">
          <div className="tab right">ONE</div>
          <div className="tab right">TWO</div>
          <div className="tab right">THREE</div>
          <div className="tab">FOUR</div>
        </div>
        {content}
      </div>
    );
  }
}

export default Tabs;
