import React, { Component } from "react";
import "./Grid.css";

class Grid extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pics: ["https://unsplash.com/photos/EwKXn5CapA4", 2, 3, 4, 5, 6, 7, 8, 9]
    };
  }
  render() {
    const articles = this.state.pics.map(pic => {
      return (
        <article>
          <figure>
            <h2>Article title</h2>
            <p>Some text about the article...</p>
          </figure>
          <img alt src={pic} />
        </article>
      );
    });
    return (
      <div className="grid">
        <h1>Auto Hexagonal CSS Grid Layout</h1>
        <section>{articles}</section>
        <svg width="0" height="0">
          <defs>
            <clipPath id="hexagono" clipPathUnits="objectBoundingBox">
              <polygon points=".25 0, .75 0, 1 .5, .75 1, .25 1, 0 .5" />
            </clipPath>
          </defs>
        </svg>
      </div>
    );
  }
}

export default Grid;
