import React, { Component } from "react";
import "./Tabs.css";
import TabContent from "./TabContent";

class Tabs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selected: "0",
      info: [
        {
          title: "some lorem ipsum",
          text: "some lorem ipsum",
          img: "../assets/0.jpg",
          id: "0"
        },
        {
          title: "some lorem ipsum",
          text: "some lorem ipsum",
          img: "../assets/1.jpg",
          id: "1"
        },
        {
          title: "some lorem ipsum",
          text: "some lorem ipsum",
          img: "../assets/2.jpg",
          id: "2"
        },
        {
          title: "some lorem ipsum",
          text: "some lorem ipsum",
          img: "../assets/3.jpg",
          id: "3"
        }
      ]
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    this.setState({
      selected: e.target.id
    });
  }

  render() {
    const content = this.state.info.map(el => {
      if (this.state.selected === el.id) {
        return (
          <TabContent title={el.title} text={el.text} img={el.img} id={el.id} />
        );
      }
    });
    return (
      <div className="tabs_wrap">
        <div className="tabs">
          <div className="tab right" id="0" onClick={this.handleChange}><p>ONE</p></div>
          <div className="tab right selected" id="1" onClick={this.handleChange}><p>TWO</p></div>
          <div className="tab right" id="2" onClick={this.handleChange}><p>THREE</p></div>
          <div className="tab" id="3" onClick={this.handleChange}><p>FOUR</p></div>
        </div>
        {content}
      </div>
    );
  }
}

export default Tabs;
