import React, { Component } from "react";
import "./News.css";
import axios from "axios";

class News extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: []
    };
  }
  componentDidMount() {
    axios
      .get(
        `https://newsapi.org/v2/top-headlines?sources=techcrunch&apiKey=5a86604b83ed474991ed1221830dd50b`
      )
      .then(res => {
        const articles = res.data;
        this.setState({ articles: articles.articles });
      });
  }

  render() {
    const topicOne =
      this.state.articles.length !== 0 ? this.state.articles[0] : "TOPIC ";
    const topicTwo =
      this.state.articles.length !== 0 ? this.state.articles[1] : "TOPIC ";
    const topicThree =
      this.state.articles.length !== 0 ? this.state.articles[2] : "TOPIC ";
    const topicFour =
      this.state.articles.length !== 0 ? this.state.articles[3] : "TOPIC ";
    const topicFive =
      this.state.articles.length !== 0 ? this.state.articles[4] : "TOPIC ";
    const topicSix =
      this.state.articles.length !== 0 ? this.state.articles[5] : "TOPIC ";

    const topbg = {
      backgroundImage: "url('../assets/newsbg.jpg')"
    };
    return (
      <div className="news">
        <div className="news-top" style={topbg}>
          <div className="news-top-first-title">TechCrunch</div>
          <div className="news-top-second-title">NEWSLETTER</div>
        </div>

        <div className="news-date">
          <p>Date: August 19, 2018.</p>
        </div>

        <div className="news-content">
          <a href={topicOne.url}>
            <div className="news-content-first">
              <div className="topic-wraper one">
                <div className="topic">{topicOne.title}</div>
              </div>
              <div className="news-author"><b>By: </b><em>{topicOne.author}</em></div>

              <div className="news-text">{topicOne.content}</div>
              <img
                className="news-content-first-pic news-image"
                alt="news-content-left-pic"
                src={topicOne.urlToImage}
              />
            </div>
          </a>
          <a href={topicTwo.url}>
            <div className="news-content-mid mid">
              <div className="topic-wraper two ">
                <div className="news-content-mid-title topic">
                  {topicTwo.title}
                </div>
              </div>
              <div className="news-author"><b>By: </b><em>{topicTwo.author}</em></div>

              <img
                className="news-content-mid-pic news-image"
                alt="news-content-mid-pic"
                src={topicTwo.urlToImage}
              />
              <div className="news-text">{topicTwo.content}</div>
            </div>
          </a>
          <a href={topicThree.url}>
            <div className="news-content-third">
              <div className="topic-wraper three">
                <div className="news-content-third-title topic">
                  {topicThree.title}
                </div>
              </div>
              <div className="news-author"><b><b>By: </b></b><em>{topicThree.author}</em></div>

              <img
                className="news-content-third-pic news-image"
                alt="item-image"
                src={topicThree.urlToImage}
              />
              <div className="news-text">{topicThree.content}</div>
            </div>
          </a>
        </div>
        <div className="news-content">
          <a href={topicFour.url}>
            <div className="news-content-mid">
              <div className="topic-wraper two">
                <div className="topic">{topicFour.title}</div>
              </div>
              <div className="news-author"><b>By: </b><em>{topicFour.author}</em></div>
              <div className="news-author"><b>Published: </b><em>{topicFive.publishedAt}</em></div>              
              <div className="news-text">{topicFour.content}</div>
              <img
                className="news-content-first-pic news-image"
                alt="news-content-left-pic"
                src={topicFour.urlToImage}
              />
            </div>
          </a>
          <a href={topicFive.url}>
            <div className="news-content-third mid">
              <div className="topic-wraper one ">
                <div className="news-content-mid-title topic">
                  {topicFive.title}
                </div>
              </div>
              <div className="news-author"><b>By: </b><em>{topicFive.author}</em></div>

              <img
                className="news-content-mid-pic news-image"
                alt="news-content-mid-pic"
                src={topicFive.urlToImage}
              />
              <div className="news-text">{topicFive.content}</div>
            </div>
          </a>
          <a href={topicSix.url}>
            <div className="news-content-mid ">
              <div className="topic-wraper two">
                <div className="news-content-third-title topic">
                  {topicSix.title}
                </div>
              </div>
              <div className="news-author"><b>By: </b><em>{topicSix.author}</em></div>

              <img
                className="news-content-third-pic news-image"
                alt="item-image"
                src={topicSix.urlToImage}
              />
              <div className="news-text">{topicSix.content}</div>
            </div>
          </a>
        </div>
      </div>
    );
  }
}

export default News;
