import React, { Component } from "react";
import "./News.css";
import axios from 'axios';


class News extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: []
    }

  }
  componentDidMount() {
    axios.get(`https://newsapi.org/v2/top-headlines?sources=techcrunch&apiKey=5a86604b83ed474991ed1221830dd50b
    `)
      .then(res => {
        const articles = res.data;
        this.setState({ articles: articles.articles });
      })
  }

  render() {
    console.log(this.state.articles);
    const topbg = {
      backgroundImage: "url('../assets/newsbg.jpg')"
    };
    return (
      <div className="news">
        <div className="news-top" style={topbg}>
          <div className="news-top-first-title">Erik Vardanyan</div>
          <div className="news-top-second-title">NEWSLETTER</div>
        </div>

        <div className="news-date">
          <p>Date: August 19, 2018.</p>
        </div>

        <div className="news-content">
          <div className="news-content-left">
            <div className="topic-wraper">
              <div className="news-content-left-title topic">TOPIC ONE</div>
            </div>
            <div className="news-content-left-text news-text">lorem ipsum</div>
            <img
              className="news-content-left-pic"
              alt="news-content-left-pic"
              src=""
            />
          </div>

          <div className="news-content-right">
            <div className="news-content-right-top">
              <div className="news-content-right-top-item">
                <div className="topic-wraper">
                  <div className="news-content-right-top-item-two-title topic">
                    TOPIC TWO{" "}
                  </div>
                </div>

                <div className="news-content-right-top-item-text news-text">
                  text{" "}
                </div>
              </div>

              <div className="news-content-right-top-item">
                <div className="topic-wraper">
                  <div className="news-content-right-top-item-three-title topic">
                    TOPIC THREE{" "}
                  </div>
                </div>

                <img
                  className="news-content-right-top-item-image"
                  alt="item-image"
                  src=""
                />
                <div className="news-content-right-top-item-text news-text">
                  text{" "}
                </div>
              </div>
            </div>

            <div className="news-content-right-bottom">
              <div className="topic-wraper">
                <div className="news-content-right-bottom-title topic">
                  KEY DATES
                </div>
              </div>

              <br />
              <div className="news-content-right-bottom-date">date </div>
              <div className="news-content-right-bottom-date">date </div>
              <div className="news-content-right-bottom-date">date </div>
              <div className="news-content-right-bottom-date">date </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default News;
