import React, { Component } from "react";
import "./News.css";
import axios from "axios";
import SecNavbar from './SecNavbar';


class News extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: []
    };
  }
  componentDidMount() {
    axios
      .get(
        `https://newsapi.org/v2/top-headlines?sources=techcrunch&apiKey=5a86604b83ed474991ed1221830dd50b`
      )
      .then(res => {
        const articles = res.data;
        this.setState({ articles: articles.articles });
      });
  }

  render() {
    let cards= [];
    for(let i=0; i<6; i++) {
      let article = this.state.articles.length !== 0 ? this.state.articles[i] : "TOPIC ";
      cards.push(<a href={one.url}>
      <div className="news-content-first">
        <div className="topic-wraper one">
          <div className="topic">{one.title}</div>
        </div>
        <div className="news-author"><b>By: </b><em>{one.author}</em></div>
        <div className="news-author"><b>Published: </b><em>{one.publishedAt}</em></div>              

        <div className="news-text">{one.content}</div>
        <img
          className="news-content-first-pic news-image"
          alt="news-content-left"
          src={one.urlToImage}
        />
      </div>
    </a >)
    }
    const one =
      this.state.articles.length !== 0 ? this.state.articles[0] : "TOPIC ";
    const two =
      this.state.articles.length !== 0 ? this.state.articles[1] : "TOPIC ";
    const three =
      this.state.articles.length !== 0 ? this.state.articles[2] : "TOPIC ";
    const four =
      this.state.articles.length !== 0 ? this.state.articles[3] : "TOPIC ";
    const five =
      this.state.articles.length !== 0 ? this.state.articles[4] : "TOPIC ";
    const six =
      this.state.articles.length !== 0 ? this.state.articles[5] : "TOPIC ";


    const topbg = {
      backgroundImage: "url('../assets/newsbg.jpg')"
    };
    return ([
      <SecNavbar/>,
      <div className="news">
        <div className="news-top" style={topbg}>
          <div className="news-top-first-title">TechCrunch</div>
          <div className="news-top-second-title">NEWSLETTER</div>
        </div>

        <div className="news-date">
          <p>Date: {one.publishedAt}</p>
        </div>

        <div className="news-content">
          <a href={one.url}>
            <div className="news-content-first">
              <div className="topic-wraper one">
                <div className="topic">{one.title}</div>
              </div>
              <div className="news-author"><b>By: </b><em>{one.author}</em></div>
              <div className="news-author"><b>Published: </b><em>{one.publishedAt}</em></div>              

              <div className="news-text">{one.content}</div>
              <img
                className="news-content-first-pic news-image"
                alt="news-content-left"
                src={one.urlToImage}
              />
            </div>
          </a >
          <a href={two.url} className="mid">
            <div className="news-content-mid ">
              <div className="topic-wraper two ">
                <div className="news-content-mid-title topic">
                  {two.title}
                </div>
              </div>
              <div className="news-author"><b>By: </b><em>{two.author}</em></div>
              <div className="news-author"><b>Published: </b><em>{two.publishedAt}</em></div>              

              <img
                className="news-content-mid-pic news-image"
                alt="news-content-mid"
                src={two.urlToImage}
              />
              <div className="news-text">{two.content}</div>
            </div>
          </a>
          <a href={three.url}>
            <div className="news-content-third">
              <div className="topic-wraper three">
                <div className="news-content-third-title topic">
                  {three.title}
                </div>
              </div>
              <div className="news-author"><b><b>By: </b></b><em>{three.author}</em></div>
              <div className="news-author"><b>Published: </b><em>{three.publishedAt}</em></div>              

              <img
                className="news-content-third-pic news-image"
                alt="item"
                src={three.urlToImage}
              />
              <div className="news-text">{three.content}</div>
            </div>
          </a>
        </div>
        <div className="news-content">
          <a href={four.url}>
            <div className="news-content-mid">
              <div className="topic-wraper two">
                <div className="topic">{four.title}</div>
              </div>
              <div className="news-author"><b>By: </b><em>{four.author}</em></div>
              <div className="news-author"><b>Published: </b><em>{four.publishedAt}</em></div>              
              <div className="news-text">{four.content}</div>
              <img
                className="news-content-first-pic news-image"
                alt="news-content-left"
                src={four.urlToImage}
              />
            </div>
          </a>
          <a href={five.url} className="mid">
            <div className="news-content-third">
