import React, { Component } from 'react';
import './Card.css';

class Card extends Component {
  render() {
    // const {title, subtitle, icon} = this.props;
    const title = this.props.title;
    const subtitle = this.props.subtitle;
    const icon = this.props.icon;

    console.log(title + " " + subtitle + " " + icon);

    const iconClass = "icon " + icon;
    const animateClass = function() {
      iconClass = iconClass + "animated rubberBand"
    }

    return (
      <div className="card">
        <img className="card_bg_image" src='../assets/card_bg.png' alt='pic'/>
        <div className="card_title">{title}</div>
        <div className="card_subtitle">{subtitle}</div>
        <i className={iconClass} onmouseover={animateClass}></i>
      </div>
    );
  }
}

export default Card;
